const today = new Date().toISOString().slice(0, 10);

// Step 1: pair each item with its index
const withIndex = items.map((item, i) => ({ json: item.json, index: i }));

// Step 2: filter using that index metadata
const filtered = withIndex.filter(({ json }) => {
  // Only Pending rows
  if (json.Status !== 'Pending') return false;

  const isToday = json.Date === today;

  // Manual always, or Automatic if today
  if (json['Publish Mode'] === 'Manual') return true;
  if (json['Publish Mode'] === 'Automatic' && isToday) return true;

  return false;
});

// Step 3: map back to the format n8n expects, plus compute row_number
return filtered.map(({ json, index }) => ({
  json: {
    ...json,
    // actual sheet row is zero-based index + 2 (header row is 1)
    row_number: index + 1,
  }
}));

